import React, { useEffect, useState } from "react";
import axios from "../../utils/axios";
import { Typography, Button, Box, Paper } from "@mui/material";

import { useSelector } from "react-redux";
import { useParams, Navigate, Link } from "react-router-dom";

function TransactionDetails() {
  const userId = useSelector((state) => state.auth.id);
  const params = useParams();
  const transactionId = params.transactionId;
  const [product, setProduct] = useState({ priceStrip: "", name: "" });
  const [transaction, setTransaction] = useState([]);
  const [address, setAddress] = useState("");
  const [invoice, setInvoice] = useState("");
  const [amount, setAmount] = useState("0");
  const [image, setImage] = useState(
    "data:image/png;base64,"
  );
  const [state, setState] = useState("");
  const [status, setStatus] = useState("");
  const [upload, setUpload] = useState("");

  const fetchTransaction = async () => {
    try {
      const res = await axios.get(`/transactions/details/${transactionId}`);
      console.log(res.data);
      setTransaction(res.data[0]);
      setAddress(res.data[0][0].address);
      setInvoice(res.data[0][0].invoice);
      setAmount(res.data[0][0].amount);
      if (res.data[0][0].paymentPhoto) {
        setImage(res.data[0][0].paymentPhoto);
      }
      setStatus(res.data[0][0].status);
    } catch (error) {
      alert(error);
    }
  };
  console.log(transaction);
  useEffect(() => {
    fetchTransaction();
  }, [state, image]);

  const onImageChange = (e) => {
    let newImage = e.target.files[0];
    setUpload(newImage);
    setImage(URL.createObjectURL(newImage));
  };

  const onUploadClick = async () => {
    try {
      const formData = new FormData();
      formData.append("paymentPhoto", upload);

      const res = await axios.put(
        `/transactions/details/${params.transactionId}`,
        formData
      );
      alert("Payment photo upload success");
      window.location.reload();
      console.log({ res });
    } catch (error) {
      // alert(error);
      console.log(error);
    }
  };

  if (!userId) {
    return <Navigate to="/" replace />;
  }
  const onCompleteClick = async () => {
    try {
      const data = { status: "complete", transaction };
      const res = axios.put(`/orders/update/${transactionId}`, data);
      alert("Order completed");
      window.location.reload();
    } catch (error) {
      console.log({ error });
    }
  };

  if (!userId) {
    return <Navigate to="/" replace />;
  }

  const renderTransaction = () => {
    return transaction.map((transaction, index) => {
      return (
        <Box>
          <Box display="flex" justifyContent="space-around" alignItems="center">
            <Box
              width="16%"
              display="flex"
              justifyContent="center"
              alignItems="center"
            >
              <Typography>{transaction.productName}</Typography>
            </Box>
            <Box
              width="16%"
              variant="h6"
              display="flex"
              justifyContent="center"
            >
              <Typography>
                Rp{transaction.productPrice.toLocaleString("id")}
              </Typography>
            </Box>
            <Box
              width="16%"
              variant="h6"
              display="flex"
              justifyContent="center"
            >
              <img
                src={transaction.productPhoto}
                alt="product image"
                style={{ height: "50%", width: "100px" }}
              />
            </Box>
            <Box
              width="16%"
              variant="h6"
              display="flex"
              justifyContent="center"
            >
              <Typography>{transaction.qty}</Typography>
            </Box>
            <Box
              width="16%"
              variant="h6"
              display="flex"
              justifyContent="center"
            >
              <Typography>{transaction.variant}</Typography>
            </Box>
            <Box
              width="16%"
              variant="h6"
              display="flex"
              justifyContent="center"
            >
              <Typography>
                Rp
                {(transaction.productPrice * transaction.qty).toLocaleString(
                  "id"
                )}
              </Typography>
            </Box>
          </Box>
          {index == transaction.length - 1 ? (
            <Box
              borderBottom={0}
              borderColor="darkgray"
              paddingBottom="12px"
              mb="20px"
            />
          ) : (
            <Box
              borderBottom={1}
              borderColor="lightgray"
              paddingBottom="12px"
              mb="20px"
            />
          )}
        </Box>
      );
    });
  };

  let button;

  if (status == "waiting payment") {
    button = (
      <div>
        <Box marginTop="10px" marginBottom="10px">
          <input type="file" onChange={onImageChange} />
        </Box>
        <Button onClick={onUploadClick} variant="contained" color="success">
          Upload
        </Button>
      </div>
    );
  } else if (status == "sending") {
    button = (
      <div>
        <Button onClick={onCompleteClick} variant="contained" color="success">
          Complete
        </Button>
      </div>
    );
  }

  return (
    <Box display="flex" justifyContent="space-around">
      <Paper
        sx={{
          width: "70%",
          backgroundColor: "white",
          marginTop: 10,
          borderRadius: 3,
          boxShadow: 3,
        }}
      >
        <Box
          display="flex"
          justifyContent="center"
          alignItems="center"
          paddingTop="1px"
        >
          <Typography variant="h4" paddingX="20px">
            {invoice} Details
          </Typography>
          {status == "reject" ? (
            <Typography
              variant="h5"
              marginX="3px"
              maxHeight="100%"
              borderRadius="3px"
              paddingX="20px"
              sx={{
                backgroundColor: "red",
                color: "white",
              }}
            >
              {status}
            </Typography>
          ) : (
            <Typography
              variant="h5"
              marginX="3px"
              maxHeight="100%"
              borderRadius="3px"
              paddingX="20px"
              sx={{
                backgroundColor: "green",
                color: "white",
              }}
            >
              {status}
            </Typography>
          )}
        </Box>
        <Box
          marginTop="20px"
          justifyContent="space-around"
          borderBottom={1}
          borderColor="darkgray"
          display="flex"
        >
          <Box width="16%" variant="h6" display="flex" justifyContent="center">
            <Typography variant="h6">Name</Typography>
          </Box>
          <Box width="16%" variant="h6" display="flex" justifyContent="center">
            <Typography variant="h6">Price</Typography>
          </Box>
          <Box width="16%" variant="h6" display="flex" justifyContent="center">
            <Typography variant="h6">Photo</Typography>
          </Box>
          <Box width="16%" variant="h6" display="flex" justifyContent="center">
            <Typography variant="h6">Quantity</Typography>
          </Box>
          <Box width="16%" variant="h6" display="flex" justifyContent="center">
            <Typography variant="h6">Variant</Typography>
          </Box>
          <Box width="16%" variant="h6" display="flex" justifyContent="center">
            <Typography variant="h6">Total Price</Typography>
          </Box>
        </Box>
        {renderTransaction()}
        <Box display="flex" justifyContent="space-around">
          <Box
            margin="auto"
            textAlign="center"
            border="solid"
            borderRadius="3px"
            borderColor="darkgray"
            width="65%"
            paddingY="3px"
          >
            <Typography variant="h5">Shipping Address</Typography>
            <Typography variant="subtitle1">{address}</Typography>
            <Typography variant="h5">
              Total: Rp{amount.toLocaleString("id")}
            </Typography>
            <Typography fontWeight="bold">
              Please pay via bank transfer (Mandiri):
              <Typography variant="h4" color="red">
                803 8098 1239 1293
              </Typography>
              <Typography variant="h4" fontWeight="bold">
                Rezeky Chan
              </Typography>
            </Typography>
          </Box>
          <Box display="flex" margin="0px 10px 10px 0px" justifyContent="end">
            <Box
              marginRight="20px"
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
            >
              <Box border="solid #d5d5d5" borderRadius="3px">
                <img src={image} alt="Payment Photo" width={320} />
              </Box>

              {button}
            </Box>
          </Box>
        </Box>
      </Paper>
    </Box>
  );
}

export default TransactionDetails;
